import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Label from 'react-bootstrap/Label';
import Input from 'react-bootstrap/Input';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

import Const from '../const.js';
import User from '../objects/user.js';
import TableCards from './card/tableCards';


const cste = new Const();

class myCards extends Component {
    constructor(props) {
        super(props);
        var pState = JSON.parse(localStorage.getItem(cste.keyPrevState));

        var u = new User();
        u.findUserById(pState.userId);

        this.state = {
            user: {
                id: u.id,
                first_name: u.first_name,
                last_name: u.last_name,
                email: u.email,
                password: u.password,
            },
            readOnly: true,
        };
    }


    render() {
        return (


            <div>
                <br /><br /><br /><br />
                <div class="bg-light rounded border container-fluid col-lg-4 col-md-8 col-sm-10" >
                    <br /><br />
                    <h1 align="center"><a class="text-dark"> Mes Cartes Bancaires </a></h1>
                    <br /><br />
                    <TableCards />

                </div>
                <br />

                <div class="bg-light rounded border container-fluid col-lg-4 col-md-8 col-sm-10" >
                    <br />
                    <Button> Ajouter une carte</Button>
                    <br />
                    <Collapse isOpen={collapse}>
                        <Form>
                            <Row form>
                                <Col md={4}>
                                    <FormGroup>
                                        <Label for="exampleEmail">Email</Label>
                                        <Input type="email" name="email" id="exampleEmail" placeholder="with a placeholder" />
                                    </FormGroup>
                                </Col>
                                <Col md={4}>
                                    <FormGroup>
                                        <Label for="examplePassword">Password</Label>
                                        <Input type="password" name="password" id="examplePassword" placeholder="password placeholder" />
                                    </FormGroup>
                                </Col>
                                <Col md={4}>
                                    <FormGroup>
                                        <Label for="examplePassword">Password</Label>
                                        <Input type="password" name="password" id="examplePassword" placeholder="password placeholder" />
                                    </FormGroup>
                                </Col>
                            </Row>
                        </Form>
                    </Collapse>

                </div>
            </div>
        );
    }
}
export default myCards;
