import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import FormGroup from 'react-bootstrap/FormGroup';
import { Collapse } from 'react-collapse';

import Const from '../const.js';
import User from '../objects/user.js';
import TableCards from './card/tableCards';


const cste = new Const();

class myCards extends Component {
    constructor(props) {
        super(props);
        var pState = JSON.parse(localStorage.getItem(cste.keyPrevState));

        var u = new User();
        u.findUserById(pState.userId);

        var date = new Date();
        var month = (date.getMonth()).toString();
        var year = (date.getFullYear()).toString();
        var minDate = year + '-' + month;
        alert(date.toString());
        this.state = {
            currentDate: minDate,
            collapse: false,
        };
    }

    toggleCollapse = () => {
        this.setState({ collapse: !this.state.collapse });
    }

    currentDate = () => {
        var date = new Date();
        var month = (date.getMonth()).toString();
        var year = (date.getFullYear()).toString();
        return (year + '-' + month);
    }


    render() {
        return (


            <div>
                <br /><br /><br /><br />
                <div class="bg-light rounded border container-fluid col-lg-4 col-md-8 col-sm-10" >
                    <br /><br />
                    <h1 align="center"><a class="text-dark"> Mes Cartes Bancaires </a></h1>
                    <br /><br />
                    <TableCards />

                </div>
                <br />

                <div class="bg-light rounded border container-fluid col-lg-4 col-md-8 col-sm-10" >
                    <br />
                    <h2 align="center"><a class="text-dark"> Ajouter une carte </a></h2>
                    <br />

                    <div class=" container col-lg-4 col-md-4 col-sm-4" >
                        <Button variant="success" onClick={() => { this.toggleCollapse() }}>
                            Ajouter une carte
                        </Button>
                    </div>
                    <br /><br />

                    <Collapse isOpened={this.state.collapse}>
                        <Form>
                            <Row form>
                                <Col md={4}>
                                    <FormGroup>
                                        <label for="last_4">NÂ° de carte</label>
                                        <Form.Control type="email" name="email" />
                                    </FormGroup>
                                </Col>
                                <Col md={4}>
                                    <FormGroup>
                                        <label for="brand">Type</label>
                                        <Form.Control type="text" name="brand" />
                                    </FormGroup>
                                </Col>
                                <Col md={4}>
                                    <FormGroup>
                                        <label for="expired_at">Expiration</label>
                                        <Form.Control type="month" name="expired_at" min={this.state.currentDate} />
                                    </FormGroup>
                                </Col>
                            </Row>

                            <br /><br />
                            <Button type="submit" variant="primary">
                                Enregistrer
                            </Button>
                            <br /><br />

                        </Form>
                    </Collapse>

                </div>
            </div>
        );
    }
}
export default myCards;
