import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';


/*
function handleEmail(event) {
        this.setState({ email: event.target.email });
    }
function handlePassword(event) {
        this.setState1({ lpassword: event.target.password });
    }

function handleConnect(event) {
        alert('Welcome to watermelon');
        event.preventDefault();
    }*/


class signUp extends Component {
    constructor(props) {
        super(props);
        this.state = {
            fname: '',
            lname: '',
            email: '',
            pwd: '',
        };
    }

    handleInputChange = (event) => {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({ [name]: value });
    }

    handleSubmit = (event) => {

    }

    writeUserFile = () => {
        const createCsvWriter = require('csv-writer').createObjectCsvWriter;
        const csvWriter = createCsvWriter({
            path: 'user.csv',
            header: [
                { id: 'fname', title: 'Fname' },
                { id: 'lname', title: 'Lname' },
                { id: 'email', title: 'Email' },
                { id: 'pwd', title: 'Password' },
            ]
        });
            /*{
                fname: this.state.fname,
                lname: this.state.lname,
                email: this.state.email,
                pwd: this.state.pwd,
            }*/
        const data = [
            {
                fname: 'test',
                lname: 'test',
                email: 'test',
                pwd: 'test',
            }
        ];

        csvWriter
            .writeRecords(data)
            .then(()=> console.log('The CSV file was written successfully'));
        alert('Bien entré dans fonction');
    }

    render() {
        return (
            <div>
                <br /><br /><br /><br />
                <div class="rounded border border-danger container-fluid col-lg-4 col-md-8 col-sm-10" >
                    <br /><br />
                    <h1 align="center"><a class="text-danger"> S'inscrire </a></h1>
                    <br /><br />
                    <Form onSubmit={this.writeUserFile}>

                        <Form.Group controlId="formBasicfname">
                            <Form.Label>Prénom</Form.Label>
                            <Form.Control
                                required name="fname"
                                value={this.state.fname}
                                onChange={this.handleInputChange}
                                type="text" placeholder="Prénom" />
                        </Form.Group>

                        <Form.Group controlId="formBasiclname">
                            <Form.Label>Nom</Form.Label>
                            <Form.Control
                                required name="lname"
                                value={this.state.lname}
                                onChange={this.handleInputChange}
                                type="text" placeholder="Nom de famille" />
                        </Form.Group>

                        <Form.Group controlId="formBasicEmail">
                            <Form.Label>Adresse email</Form.Label>
                            <Form.Control
                                required name="email"
                                value={this.state.email}
                                onChange={this.handleInputChange}
                                type="email" placeholder="Entrer email" />
                        </Form.Group>

                        <Form.Group controlId="formBasicPassword">
                            <Form.Label>Mot de Passe</Form.Label>
                            <Form.Control
                                required name="pwd"
                                value={this.state.pwd}
                                onChange={this.handleInputChange}
                                type="password" placeholder="Mot de Passe" />
                        </Form.Group>

                        <br />
                        <Form.Group controlId="formBasicButton">
                            <Button variant="danger" type="submit">
                                Inscription
                            </Button>
                        </Form.Group>

                    </Form >
                    <br /><br />
                </div>
            </div>
        );
    }

}
export default signUp;
