import React, { Component } from 'react';
import './App.css';
import Header from './Header.js';
import Body from './Body.js';
import Const from './const.js';

var prevState;
var cste = new Const();

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userId: -1,
            page: 'signOut'
        };
    }

    handleChange = (e) => {
        this.setState({ userId: e.userId, page: e.page });
    }

    componentDidUpdate() {
        prevState = localStorage.getItem(cste.keyPrevState);
        localStorage.setItem(cste.keyPrevState, JSON.stringify(this.state));
    }

    componentDidMount() {
        prevState = JSON.parse(localStorage.getItem(cste.keyPrevState));
        if (prevState !== null) {
            if (prevState.page !== 'signOut') {
                this.setState(JSON.parse(localStorage.getItem(cste.keyPrevState)));
            }
        }
    }

    footer = () => {
        return (
            <div class="bg-light position-sticky">
                <div align="center">
                    <br /><br /><br />
                    <h4>Auteurs: </h4><br />
                    Henintsoa RAZAFINDRAZAKA <br /><br />
                    Noha ZAHRAN
                <br /><br /><br />
                </div>
            </div>
        );
    }

    render() {
        return (
            <div>
                <Header
                    userId={this.state.userId}
                    onChange={this.handleChange}

                />
                <Body
                    userId={this.state.userId}
                    page={this.state.page}
                    onChange={this.handleChange}
                />
                <br /><br /><br /><br /><br />

                {this.footer()}
            </div>
        );
    }

}

export default App;
