import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Const from '../../const.js';
import User from '../../objects/user.js';

const cste = new Const();

class lineCard extends Component {
    constructor(props) {
        super(props);
        var pState = JSON.parse(localStorage.getItem(cste.keyPrevState));
        var u = new User();
        u.findUserById(pState.userId);

        var c = this.props.card;

        this.state = {
            id: c.id,
            last_4: c.last_4,
            brand: c.brand,
            expire_at: c.expire_at,
            userId: u.id,

            readOnly: true,
            modify: '',
            save: 'none'
        };
    }

    toggleMode = () =>{
        if(this.state.modify === ''){
            this.setState({modify: 'none', save: '', readOnly: false});
        } else {
            this.setState({modify: '', save: 'none', readOnly: true});
        }
    }


    render() {
        return (
            <tr>
                <td align="center">
                    <Form.Control type="text" style={{ color: 'white' }}  
                        name="last_4" plaintext  
                        readOnly={this.state.readOnly}/>
                </td>

                <td align="center">
                    <Form.Control as="select" name="brand" plaintext
                        onChange={this.handleInputChange} style={{ color: 'white' }}
                        readOnly={this.state.readOnly}>

                        <option style={{ color:'grey' }}>Visa</option>
                        <option>Master Card</option>
                        <option>American Express</option>
                        <option>Union Pay</option>
                        <option>JCB</option>
                    </Form.Control>
                </td>

                <td align="center">
                    <Form.Control type="month" name="expire_at" 
                        plaintext style={{ color: 'white' }}
                        readOnly={this.state.readOnly} />
                </td>
                
                <td align="center">
                    <Button style={{ display: this.state.modify }} variant="secondary"
                    onClick={this.toggleMode} >
                        Modifier
                        </Button>
                        
                    <Button style={{ display: this.state.save }} variant="primary"
                        onClick={this.toggleMode} >
                        Enregistrer
                        </Button>

                    <Button variant="danger">Supprimer</Button>
                </td>
            </tr>
        );
    }
}
export default lineCard;
