import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Table from 'react-bootstrap/Table';
import Transfer from '../../objects/transfer';
import { Collapse } from 'react-collapse';


class myTransfers extends Component {
    constructor(props) {
        super(props);
        var t = new Transfer();
        var userId = this.props.userId;
        this.state = {
            dest:'',
            collapse: false,
            debited_wallet_id: null,
            credited_wallet_id: null,
            amount: null,
            userId: userId,
            tab: t.getTransfersByWallet_id(userId),
        }
    }

    toggleCollapse = () => {
        this.setState({ collapse: !this.state.collapse });
    }

    tabTransfers = () => {
        let listItem = this.state.tab.map((transfer, index) =>
            <tr>
                <td align="center"> {index} </td>
                <td align="center"> {transfer.debited_wallet_id} </td>
                <td align="center"> {transfer.credited_wallet_id} </td>
                <td align="center"> {(transfer.amount)}</td>
            </tr>
        );
        return (<tbody>{listItem}</tbody>);
    }

    handleInputChange = (event) => {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({ [name]: value });

    }

    save = () => {
        this.toggleCollapse();
        var t = new Transfer();

        // sauver dans Localstorage et ajouter au tab[]
        this.setState(state => {
            var newId = t.getNewId();
            t.id = newId;
            t.copy(state);
            t.save();

            const list = state.tab.concat({ t });
            return { tab: list };
        });
    }

    render() {
        return (

            <div class="bg-light rounded border container-fluid col-lg-8 col-md-11 col-sm-10" >
                <br /><br />
                <h1 align="center"><a class="text-warning"> Mes Dépôts </a></h1>
                <br /><br />

                <Table striped bordered hover variant="warning">
                    <thead>
                        <th>#id</th>
                        <th>De la part de...</th>
                        <th>à destination de...</th>
                        <th>Montant (€)</th>
                    </thead>

                    
                        {this.tabTransfers()}
                </Table>

                <div align="center" >
                    <Button variant="warning" onClick={this.toggleCollapse}>
                        Effectuer un dépôt
                    </Button>
                </div>
                <br /><br />

                <Collapse isOpened={this.state.collapse}>
                    <hr />
                    <Row>
                        <Col md={4}>
                        <Form.Control
                                required onChange={this.handleInputChange}
                                name="dest" type="email"
                                placeholder="Destinataire " value={this.state.dest}
                            />
                        </Col>
                        <Col md={4}>
                            <Form.Control
                                required onChange={this.handleInputChange}
                                name="amount" type="number" min="0" step="0.01"
                                placeholder="Montant  € " value={this.state.amount}
                            />
                        </Col>
                        <Col md={4}>
                            <Button variant="primary" onClick={() => { this.save() }} >
                                Enregistrer
                            </Button>
                        </Col>
                    </Row>
                    <br /><br />
                </Collapse>

            </div>


        );
    }

}
export default myTransfers;
