import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Const from '../../const.js';
import User from '../../objects/user.js';

const cste = new Const();

class lineCard extends Component {
    constructor(props) {
        super(props);
        var pState = JSON.parse(localStorage.getItem(cste.keyPrevState));

        var u = new User();
        u.findUserById(pState.userId);

        this.state = {
            id: 0,
            last_4: '',
            brand: '',
            expire_at: '',
            userId: u.id,

            readOnly: true,
            editMode: 'none'
        };
    }


    render() {
        return (
            <tr>
                <td align="center">
                    <Form.Control 
                        style={{color: 'white'}} 
                        type="text" name="last_4" 
                        plaintext />
                </td>

                <td align="center">
                    <Form.Control as="select" name="brand" plaintext onChange={this.handleInputChange}>
                        <option>Visa</option>
                        <option>Master Card</option>
                        <option>American Express</option>
                        <option>Union Pay</option>
                        <option>JCB</option>
                    </Form.Control>
                </td>

                <td align="center">
                    <Form.Control  type="month" name="expire_at" plaintext />
                </td>
                <td align="center">
                    <Button style={{ display: '' }} variant="secondary">Modifier</Button>
                    <Button style={{ display: '' }} variant="primary">Enregistrer</Button>
                    <Button variant="danger">Supprimer</Button>
                </td>
            </tr>
        );
    }
}
export default lineCard;
