import React, { Component } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import FormGroup from 'react-bootstrap/FormGroup';
import UncontrolledCollapse  from 'reactstrap ';

import Const from '../const.js';
import User from '../objects/user.js';
import TableCards from './card/tableCards';


const cste = new Const();

class myCards extends Component {
    constructor(props) {
        super(props);
        var pState = JSON.parse(localStorage.getItem(cste.keyPrevState));

        var u = new User();
        u.findUserById(pState.userId);

        this.state = {
            collapse: true,
        };
    }

    /*
    <Form>
    <Row form>
        <Col md={4}>
            <FormGroup>
                <label for="exampleEmail">Email</label>
                <Form.Control type="email" name="email" id="exampleEmail" placeholder="with a placeholder" />
            </FormGroup>
        </Col>
        <Col md={4}>
            <FormGroup>
                <label for="examplePassword">Password</label>
                <Form.Control type="password" name="password" id="examplePassword" placeholder="password placeholder" />
            </FormGroup>
        </Col>
        <Col md={4}>
            <FormGroup>
                <label for="examplePassword">Password</label>
                <Form.Control type="password" name="password" id="examplePassword" placeholder="password placeholder" />
            </FormGroup>
        </Col>
    </Row>
</Form>
*/

    toggleCollapse = () => {
        this.setState({collapse: !this.state.collapse});
        alert(this.state.collapse);
    }


    render() {
        return (


            <div>
                <br /><br /><br /><br />
                <div class="bg-light rounded border container-fluid col-lg-4 col-md-8 col-sm-10" >
                    <br /><br />
                    <h1 align="center"><a class="text-dark"> Mes Cartes Bancaires </a></h1>
                    <br /><br />
                    <TableCards />

                </div>
                <br />

                <div class="bg-light rounded border container-fluid col-lg-4 col-md-8 col-sm-10" >
                    <br />
                    <Button id="toggler"> Ajouter une carte</Button>
                    <br />
                    <UncontrolledCollapse  toggler="#toggler">
                        <p>test</p>

                    </UncontrolledCollapse >

                </div>
            </div>
        );
    }
}
export default myCards;
