import React, { Component } from 'react';


///////////////////////////////////////////////////

class signIn extends React.Component {
    constructor(props) {
      super(props);
      this.state = {email: ''};
      this.state1 = {password: ''};
      this.handleEmail = this.handleEmail.bind(this);
      this.handlePassword = this.handlePassword.bind(this);
      this.handleConnect = this.handleConnect.bind(this);
    }
  
    handleEmail(event) {
      this.setState({email: event.target.email});
    }
    handlePassword(event) {
      this.setState1({lpassword: event.target.password});
    }
  
    handleConnect(event) {
      alert('Welcome to watermelon');
      event.preventDefault();
    }
  
    render() {
      return (
        <form onSubmit={this.handleConnect}>
          <div>
          <label>
            E-Mail:
            <input type="text" email={this.state.email} onChange={this.handleEmail} />
          </label>
          </div>
          <div>
          <label>
            Password:
            <input type="text" password={this.state.password} onChange={this.handlePassword} />
          </label>
          </div>
          <div>
          < button onClick= {this.handleConnect} > Connect</ button >
          </div>
          <div>
          < button onClick= {this.handleSubmit} > Forget My Password</ button >
          </div>
        </form>
      );
    }
  }
