import React, { Component } from 'react';

function loadUsers() {
    var listUsers = [];
    var i = 0;
    var key = 'u'.concat(i.toString());
    while(localStorage.getItem(key)){
        listUsers.push(localStorage.getItem(key));
        i++;
        key = 'u'.concat(i.toString());
    }
    return listUsers;
}export default loadUsers;
