import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Const from '../../const.js';
import Card from '../../objects/card.js';
import { Collapse } from 'react-collapse';


class myPayins extends Component {
    constructor(props) {
        super(props);
        this.state = {
            collapse: false,
            amount: 0,
        }
    }

    toggleCollapse = () => {
        this.setState({ collapse: !this.state.collapse });
    }

    tabPayins = () => {

        return null;
    }

    handleInputChange = (event) => {
        const target = event.target;
        const value = target.value;
        const name = target.name;

        this.setState({ [name]: value });

    }

    render() {
        return (

            <div class="bg-light rounded border container-fluid col-lg-8 col-md-11 col-sm-10" >
                <br /><br />
                <h1 align="center"><a class="text-success"> Mes Dépôts </a></h1>
                <br /><br />

                {this.tabPayins}

                <div align="center" >
                    <Button variant="success" onClick={this.toggleCollapse}>
                        Effectuer un dépôt
                    </Button>
                </div>
                <br /><br />

                <Collapse isOpened={this.state.collapse}>
                    <Row>
                        <Col md={4}>
                            <label >Montant</label>
                        </Col>
                        <Col md={4}>
                            <Form.Control
                                required onChange={this.handleInputChange}
                                name="amount" type="number" min="0"
                            />
                        </Col>
                        <Col md={4}>
                            <Button type="submit" variant="primary">
                                Enregistrer
                            </Button>
                        </Col>
                    </Row>
                </Collapse>

            </div>


        );
    }

}
export default myPayins;
